(92, '2005-11-03', '4º Encuentro Biosolidario x Misiones Rurales Argentinas ', '', '', '<p><strong>Lugar: </strong>Ciudad aut&oacute;noma de Buenos Aires.<br />\r\n<strong>Entidad beneficiaria:</strong> Misiones Rurales Argentinas (MiRA).<br />\r\n<strong>Objetivo:</strong> recaudar fondos para permitir la continuidad de los proyectos en las 600 escuelas que apadrina MiRA.<br />\r\n<strong>Monto recaudado:</strong> U$D 110.737<br />\r\n<strong>Materializaci&oacute;n del objetivo:</strong> los fondos fueron usados para permitir la continuidad de los proyectos durante el 2006.</p>\r\n', 'rse', 'es'),
(93, '2005-10-07', '2º Encuentro Biosolidario de Uruguay ', '', '', '<p><strong>Lugar:</strong> Uruguay<br />\r\n<strong>Entidad beneficiaria:</strong> Proyecto Renacer (obra sin fines de lucro dirigida por el Padre Gustavo Larrique, se encuentra en los departamentos de Florida y Montevideo)<br />\r\n<strong>Objetivo:</strong> Construcci&oacute;n de una casa en Montevideo para alojar a los familiares de las personas en tratamiento<br />\r\n<strong>Monto recaudado: </strong>U$D 41.040<br />\r\n<strong>Materializaci&oacute;n del objetivo:</strong> los fondos fueron usados para permitir la continuidad durante el 2006 del proyecto de rehabilitaci&oacute;n de adolescentes y j&oacute;venes con problemas de adicci&oacute;n a nivel nacional.</p>\r\n', 'rse', 'es'),
